mapper.select:
select count(1)
from phis_chss.v_fha_person_main
where stateid=1
    AND  managedcentercode = #{unitId}
    AND  createdtime >=  #{startDate}
    AND  createdtime <  #{endDate}

select count(1)
from phis_chss.v_fha_person_main
where stateid=1 and verify=1
    AND  managedcentercode = #{unitId}
    AND  createdtime >=  #{startDate}
    AND  createdtime <  #{endDate}

select count(managedcentercode)
from phis_chss.v_fha_person_main
where birthday <=  add_months(sysdate,-12*65)
    AND  managedcentercode = #{unitId}
    AND  createdtime >=  #{startDate}
    AND  createdtime <  #{endDate}

select count(1)
from phis_chss.v_fha_person_main
where code in(
select code from (select * from ( select row_number()over(partition by c.code order by c.examdate desc)num,c.*
from phis_chss.v_fhc_check_main c  left join  phis_chss.v_fha_person_main  p on c.code=p.code where p.LINKB is not null and p.birthday<= add_months(sysdate,-12*65)) s
where num=1 ) where (
((rsbp =0 or rsbp is null) and (RDBP =0 or RDBP is null) and lsbp <>  0 and ldbp<> 0 and lsbp< 150 and LDBP<90)
 or ((lsbp =0 or lsbp is null) and (ldbp =0 or ldbp is null) and rsbp <> 0 and rdbp<> 0 and RSBP<150 and RDBP<90   )
  or (
  lsbp <>  0 and ldbp<> 0 and lsbp< 150 and LDBP<90
  and rsbp <> 0 and rdbp<> 0 and RSBP<150 and RDBP<90
  )
  )
    AND  examdate >=  #{startDate}


    AND  examdate <  #{endDate}

union all
select code from (select * from ( select row_number()over(partition by c.code order by c.examdate desc)num,c.*
from phis_chss.v_fhc_check_main c  left join  phis_chss.v_fha_person_main  p on c.code=p.code where p.LINKB is not null and p.birthday>add_months(sysdate,-12*65)) s
where num=1 )
where
(
((rsbp =0 or rsbp is null) and (RDBP =0 or RDBP is null) and lsbp <>  0 and ldbp<> 0 and lsbp< 140 and LDBP<90)
 or ((lsbp =0 or lsbp is null) and (ldbp =0 or ldbp is null) and rsbp <> 0 and rdbp<> 0 and RSBP<140 and RDBP<90   )
  or (
  lsbp <>  0 and ldbp<> 0 and lsbp< 140 and LDBP<90
  and rsbp <> 0 and rdbp<> 0 and RSBP<140 and RDBP<90
  )
  )
    AND  examdate >=  #{startDate}

    AND  examdate <  #{endDate}

)
    AND  managedcentercode = #{unitId}

select count(1)
from phis_chss.v_fha_person_main
where linkb is not null
and code in (
select code from phis_chss.v_fhc_check_main where
examdate >=  #{startDate}
AND  examdate <  #{endDate}
group by code
)
    AND  managedcentercode = #{unitId}

select COALESCE (SUM(gf) ,0) from phis_chss.v_report_year_blood where
1=1

and reportyear in

   #{item}


and porgcode= #{unitId}

select COALESCE (SUM(sf) ,0) from phis_chss.v_report_year_blood where
1=1

    and reportyear in

        #{item}


and porgcode=#{unitId}

select count(1)
from phis_chss.v_fha_person_main
where linkd is not null
   and code in (
select c.code from phis_chss.v_fhc_check_main c
JOIN phis_chss.v_fhc_check_attach1 ca on c.chid=ca.linkid
where
ca.createdtime >=  #{startDate}
AND  ca.createdtime <  #{endDate}
group by c.code
)
    AND  managedcentercode = #{unitId}

select count(1)
from phis_chss.v_fha_person_main pm
join phis_chss.v_fhc_check_main pc on pm.CODE=pc.CODE
where pc.CHID in (
select linkid from (select * from ( select row_number()over(partition by c.code order by c.examdate desc)num,ca.*
from phis_chss.v_fhc_check_attach1 ca join phis_chss.v_fhc_check_main c on ca.LINKID=c.CHID left join  phis_chss.v_fha_person_main  p on c.code=p.code where p.LINKd is not null) s
where num=1 )
where fbgdl1 <7
and fbgdl1<>0

    AND  createdtime >=  #{startDate}

    AND  createdtime <  #{endDate}

)

    AND  managedcentercode = #{unitId}

select COALESCE (SUM(gf) ,0) from phis_chss.v_report_year_diabetes where
1=1

    and reportyear in

        #{item}


   and porgcode= #{unitId}

select COALESCE (SUM(sf) ,0) from phis_chss.v_report_year_diabetes where
1=1

    and reportyear in

       #{item}


 and porgcode=#{unitId}

select count(1) from phis_chss.v_fha_person_main where code in (
    select code from phis_chss.v_fhd_diabetes_visit where sftransorg=0

    AND  visitdate >=  #{startDate}

    AND  visitdate <  #{endDate}

    group by code)

    AND  managedcentercode = #{unitId}

select count(1) from phis_chss.v_fha_person_main where code in (
select code from phis_chss.v_fhd_diabetes_visit where 1=1

    AND  visitdate >=  #{startDate}

    AND  visitdate <  #{endDate}

group by code)

    AND  managedcentercode = #{unitId}

select count(1) from phis_chss.v_fha_person_main where code in (
select code from phis_chss.v_fhd_diabetes_visit where 1=1

    AND  visitdate >=  #{startDate}

    AND  visitdate <  #{endDate}

group by code)

    AND  managedcentercode = #{unitId}
