mapper.select:
select count(*) from outhospitalrecord a
OHREC_DREC = '1' and a.IHD_DIS_CODE in ('E04.904', 'E04.000', 'E04.103', 'E04.903', 'E04.104', 'E04.800', 'E04.102', 'E04.900', 'E04.901', 'E04.201', 'E04.200', 'E04.100', 'E04.801', 'E04.902', 'E04.001', 'E04.101')
and OUT_HP_DT >= #{startTime}
and OUT_HP_DT < #{endTime}
and ORGAN_CODE = #{unitId}

SELECT count(*) FROM outhospitalrecord a where a.OHREC_DREC = '1' and a.IHD_DIS_CODE in ('E04.904', 'E04.000', 'E04.103', 'E04.903', 'E04.104', 'E04.800', 'E04.102', 'E04.900', 'E04.901', 'E04.201', 'E04.200', 'E04.100', 'E04.801', 'E04.902', 'E04.001', 'E04.101')
and a.OUT_HP_DT >= #{startTime}
and a.OUT_HP_DT < #{endTime}
and ORGAN_CODE = #{unitId}
AND EXISTS ( SELECT * FROM outhospitalrecord b WHERE b.CARDNO = a.CARDNO AND b.OUT_HP_DT != a.OUT_HP_DT and a.OUT_HP_DT >= date_add(b.IN_HP_DT, INTERVAL - 31 DAY) and a.OUT_HP_DT < DATE(b.IN_HP_DT)
)
