		SELECT year, 
		   month,
		   week_month AS weekMonth,
	       SUM(event_num) AS eventNum,
	       SUM(total_num) AS totalNum
		FROM data_processed_day
		WHERE event_id=#{eventId}
		  AND serial_number>=#{start}
		  AND serial_number<=#{end}

			AND unit_id NOT IN

