mapper.select:
	  select count(*) from outhospitalrecord

			OHREC_DREC = '1'
            and IHD_DIS_CODE in (select major_code from classify_code_disease where major_code like 'T00%' or major_code like 'T01%'
            or major_code like 'T02%' or major_code like 'T03%' or major_code like 'T04%' or major_code like 'T05%'
            or major_code like 'T06%' or major_code like 'T07%')

				and OUT_HP_DT >= #{startTime}


				and OUT_HP_DT < #{endTime}


				and ORGAN_CODE  =  #{unitId}

SELECT count(*) FROM outhospitalrecord a where a.OHREC_DREC = '1' and a.IHD_DIS_CODE in (select major_code from classify_code_disease where major_code like 'T00%' or major_code like 'T01%' or major_code like 'T02%' or major_code like 'T03%' or major_code like 'T04%' or major_code like 'T05%' or major_code like 'T06%' or major_code like 'T07%')
and a.OUT_HP_DT >= #{startTime}
and a.OUT_HP_DT < #{endTime}
and ORGAN_CODE = #{unitId}
AND EXISTS ( SELECT * FROM outhospitalrecord b WHERE b.CARDNO = a.CARDNO AND b.OUT_HP_DT != a.OUT_HP_DT and a.OUT_HP_DT >= date_add(b.IN_HP_DT, INTERVAL - 31 DAY) and a.OUT_HP_DT < DATE(b.IN_HP_DT)
)
