mapper.select:
select count(*) from outhospitalrecord
OHREC_DREC = '1' and IHD_DIS_CODE in (select major_code from classify_code_disease where major_code like 'K25.0%' or major_code like 'K25.2%' or major_code like 'K25.4%' or major_code like 'K25.6%' or major_code like 'K26.0%' or major_code like 'K26.2%' or major_code like 'K26.4%' or major_code like 'K26.6%' or major_code like 'K27.0%' or major_code like 'K27.2%' or major_code like 'K27.4%' or major_code like 'K27.6%' or major_code like 'K28.0%' or major_code like 'K28.2%' or major_code like 'K28.4%' or major_code like 'K28.6%' or major_code like 'K29.0%' or major_code like 'K92.2%')
and OUT_HP_DT >= #{startTime}
and OUT_HP_DT < #{endTime}
and ORGAN_CODE = #{unitId}

SELECT count(*) FROM outhospitalrecord a where a.OHREC_DREC = '1' and a.IHD_DIS_CODE in (select major_code from classify_code_disease where major_code like 'K25.0%' or major_code like 'K25.2%' or major_code like 'K25.4%' or major_code like 'K25.6%' or major_code like 'K26.0%' or major_code like 'K26.2%' or major_code like 'K26.4%' or major_code like 'K26.6%' or major_code like 'K27.0%' or major_code like 'K27.2%' or major_code like 'K27.4%' or major_code like 'K27.6%' or major_code like 'K28.0%' or major_code like 'K28.2%' or major_code like 'K28.4%' or major_code like 'K28.6%' or major_code like 'K29.0%' or major_code like 'K92.2%')
and a.OUT_HP_DT >= #{startTime}
and a.OUT_HP_DT < #{endTime}
and ORGAN_CODE = #{unitId}
AND EXISTS ( SELECT * FROM outhospitalrecord b WHERE b.CARDNO = a.CARDNO AND b.OUT_HP_DT != a.OUT_HP_DT and a.OUT_HP_DT >= date_add(b.IN_HP_DT, INTERVAL - 31 DAY) and a.OUT_HP_DT < DATE(b.IN_HP_DT)
)
