mapper.select:
	  select count(*) from outhospitalrecord

			OHREC_DREC = '1'
            and  IHD_DIS_CODE in (select major_code from classify_code_disease where major_code like 'I05%' or major_code like 'I06%'
            or major_code like 'I07%' or major_code like 'I08%' or major_code like 'I09%' or major_code like 'I11%'
            or major_code like 'I12%' or major_code like 'I13%' or major_code like 'I20%' or major_code like 'I21%'
            or major_code like 'I22%' or major_code like 'I23%' or major_code like 'I24%' or major_code like 'I25%')
            and IHD_DIS_CODE_1 in (select major_code from classify_code_disease where major_code like 'I50%')

				and OUT_HP_DT >= #{startTime}


				and OUT_HP_DT < #{endTime}


				and ORGAN_CODE  =  #{unitId}

SELECT count(*) FROM outhospitalrecord a where a.OHREC_DREC = '1' and a.IHD_DIS_CODE in (select major_code from classify_code_disease where major_code like 'I05%' or major_code like 'I06%' or major_code like 'I07%' or major_code like 'I08%' or major_code like 'I09%' or major_code like 'I11%' or major_code like 'I12%' or major_code like 'I13%' or major_code like 'I20%' or major_code like 'I21%' or major_code like 'I22%' or major_code like 'I23%' or major_code like 'I24%' or major_code like 'I25%') and a.IHD_DIS_CODE_1 in (select major_code from classify_code_disease where major_code like 'I50%')
and a.OUT_HP_DT >= #{startTime}
and a.OUT_HP_DT < #{endTime}
and ORGAN_CODE = #{unitId}
AND EXISTS ( SELECT * FROM outhospitalrecord b WHERE b.CARDNO = a.CARDNO AND b.OUT_HP_DT != a.OUT_HP_DT and a.OUT_HP_DT >= date_add(b.IN_HP_DT, INTERVAL - 31 DAY) and a.OUT_HP_DT < DATE(b.IN_HP_DT)
)
