SELECT e.sickness_classify AS sicknessClassify, coalesce(sum(o.school_section in('1','7')),0) AS nurseries, coalesce(sum(o.school_section = '2'),0) AS primarySchool, coalesce(sum(o.school_section in('3','4','5','6','8')),0) AS juniorUpperSchool, ROUND((coalesce(sum(o.school_section in('1','7')),0)/count(1))*100,2) AS nurseriesRate, ROUND((coalesce(sum(o.school_section = '2'),0)/count(1))*100,2) AS primarySchoolRate, ROUND((coalesce(sum(o.school_section in('3','4','5','6','8')),0)/count(1))*100,2) AS juniorUpperSchoolRate, count(1) AS subtotal, (SELECT count(1) FROM epidemic_situation)as total FROM epidemic_situation AS e INNER JOIN org_school_people AS o ON e.grade = o.id INNER JOIN org_school as s ON o.school_id = s.id where 1=1
AND s.business_district in
#{item}
AND e.suspend_data >= #{startTime}
AND e.suspend_data <= #{endTime}
GROUP BY e.sickness_classify;

SELECT coalesce(sum(o.school_section in('1','7')),0) AS nurseries, coalesce(sum(o.school_section = '2'),0) AS primarySchool, coalesce(sum(o.school_section in('3','4','5','6','8')),0) AS juniorUpperSchool, coalesce(SUM(e.suspend_people_number),0) AS total FROM epidemic_situation AS e INNER JOIN org_school_people AS o ON e.grade = o.id INNER JOIN org_school as s ON o.school_id = s.id where 1=1
AND s.business_district in
#{item}
AND e.suspend_data >= #{startTime}
AND e.suspend_data <= #{endTime}
AND e.sickness_classify = #{sicknessClassify}

SELECT e.sickness_classify AS sicknessClassify, sum(if(o.school_section in('1','7'),e.total,0))AS nurseries, sum(if(o.school_section = '2',e.total,0))AS primarySchool, sum(if(o.school_section in('3','4','5','6','8'),e.total,0))AS juniorUpperSchool, ROUND(sum(if(o.school_section in('1','7'),e.total,0))/SUM(e.total)*100,2) AS nurseriesRate, ROUND(sum(if(o.school_section = '2',e.total,0))/SUM(e.total)*100,2) AS primarySchoolRate, ROUND(sum(if(o.school_section in('3','4','5','6','8'),e.total,0))/SUM(.e.total)*100,2) AS juniorUpperSchoolRate, SUM(e.total) AS subtotal, (SELECT SUM(total) FROM epidemic_situation)as total FROM epidemic_situation AS e INNER JOIN org_school_people AS o ON e.grade = o.id INNER JOIN org_school as s ON o.school_id = s.id where 1=1
AND s.business_district in
#{item}
AND e.suspend_data >= #{startTime}
AND e.suspend_data <= #{endTime}
GROUP BY e.sickness_classify;

