SELECT e.sickness_classify AS sicknessClassify, coalesce(sum(o.school_section in('1','7')),0) AS nurseries, coalesce(sum(o.school_section = '2'),0) AS primarySchool, coalesce(sum(o.school_section in('3','4','5','6','8')),0) AS juniorUpperSchool, count(1) AS subtotal, coalesce(sum(if(o.school_section in('1','7'),e.total,0)),0)AS nurseries1, coalesce(sum(if(o.school_section = '2',e.total,0)),0)AS primarySchool1, coalesce(sum(if(o.school_section in('3','4','5','6','8'),e.total,0)),0)AS juniorUpperSchool1, SUM(e.total) AS subtotal1, coalesce(sum(if(o.school_section in('1','7'),e.suspend_people_number,0)),0)AS nurseries2, coalesce(sum(if(o.school_section = '2',e.suspend_people_number,0)),0)AS primarySchool2, coalesce(sum(if(o.school_section in('3','4','5','6','8'),e.suspend_people_number,0)),0)AS juniorUpperSchool2, SUM(e.first_week) AS subtotal2, coalesce(sum(if(o.school_section in('1','7'),e.total,0)),0)AS nurseries3, coalesce(sum(if(o.school_section = '2',e.total,0)),0)AS primarySchool3, coalesce(sum(if(o.school_section in('3','4','5','6','8'),e.total,0)),0)AS juniorUpperSchool3, ROUND((SUM(e.total)/SUM(e.suspend_people_number))*100,2) AS lhlxj FROM epidemic_situation AS e INNER JOIN org_school_people AS o ON e.grade = o.id INNER JOIN org_school AS s ON o.school_id = s.id where 1=1
AND s.business_district in
#{item}
AND e.suspend_data >= #{startTime}
AND e.suspend_data <= #{endTime}
GROUP BY e.sickness_classify;

