mapper.select:
SELECT e.id, n.school_full_name AS incidentUnit, e.report_unit AS reportUnit, e.school_year as schoolYear, e.sickness_classify AS sicknessClassify, e.grade AS grade, e.region AS region, e.create_time AS createTime FROM epidemic_situation AS e LEFT JOIN org_school_name AS n ON e.incident_unit=n.id WHERE 1 = 1
AND n.school_full_name LIKE CONCAT('%',#{incidentUnit},'%')
AND e.report_unit =#{reportUnit}
AND e.region =#{region}
AND e.sickness_classify =#{sicknessClassify}
ORDER BY e.create_time DESC

    	SELECT n.school_full_name name,count(1) value
    	FROM epidemic_situation t
    	inner join org_school s on t.incident_unit=s.parent_school_id
		LEFT JOIN org_school_name n ON t.incident_unit=n.id
		where 1=1

			AND t.school_year=#{schoolYear}


			AND s.district=#{district}


			AND t.sickness_classify =#{sicknessClassify}

		GROUP BY t.incident_unit
    	ORDER BY value DESC

    	SELECT n.school_full_name name,sum(t.total) value
    	FROM epidemic_situation t
    	inner join org_school s on t.incident_unit=s.parent_school_id
		LEFT JOIN org_school_name n ON t.incident_unit=n.id
		where 1=1

			AND t.school_year=#{schoolYear}


			AND s.district=#{district}


			AND t.sickness_classify =#{sicknessClassify}

		GROUP BY t.incident_unit
    	ORDER BY value DESC

SELECT s.school_full_name as schoolName, count(1) total, sum(is_early_warning_value = '1'AND is_reason_traceability = '1') as count FROM epidemic_situation AS e LEFT JOIN org_school_name AS s ON e.incident_unit = s.id where 1=1
and e.region =#{region}
and e.school_year =#{schoolYear}
GROUP BY incident_unit;

select t.affiliation AS grouping, e.sickness_classify AS sicknessClassify, t.parent_school_id AS schoolId, p.id AS orgSchoolPeopleId, p.total AS orgSchoolPeopleTotal, e.grade AS incidentOrgSchoolPeopleId, e.total AS incidentTotol, t.total AS shcoolTotal from epidemic_situation e inner join org_school t on e.incident_unit=t.parent_school_id inner join org_school_people p on t.id=p.school_id where 1=1 and t.affiliation in
#{affiliation}
and e.school_year =#{schoolYear}
and t.district =#{district}
