mapper.select:
SELECT ORGAN_CODE AS unitId, sum(FD_NUMB+ED_NUMB) AS oPDCount from op_workload where 1=1  

 AND ORGAN_CODE = #{query.unitId}


and REP_YEAR = #{query.year} and REP_TYPE= #{query.month}

GROUP BY ORGAN_CODE

select unit_id AS unitId, sum(up_out_number) AS inPersonCount,sum(down_out_number) AS OutPersonCount from count_referral where 1=1  

 AND unit_id = #{query.unitId}


 AND create_time LIKE CONCAT(#{query.date},'%') 

GROUP BY unit_id;

SELECT concat(REP_YEAR,'-',REP_TYPE) AS typeMonth, sum(FD_NUMB+ED_NUMB) AS oPDCount from op_workload where 1=1  

 AND ORGAN_CODE = #{query.unitId}


and REP_YEAR = #{query.year} and REP_TYPE= #{query.month}

GROUP BY REP_YEAR,REP_TYPE;

SELECT DATE_FORMAT(HP_DATE,'%Y-%m') AS typeMonth, COUNT(1) AS inPatientCount FROM inhospitalrecord where 1=1 

 AND ORGAN_CODE = #{query.unitId}


 AND HP_DATE LIKE CONCAT(#{query.date},'%') 

GROUP BY DATE_FORMAT(HP_DATE,'%Y-%m');
